void test_square()
{
  wprintf(L"test_square\n");

  int dest[5] = {0,0,0,0,0};
  int v[5]    = {1,4,6,12,4};
  int rows[]  = {0,3,6,8,11,13};
  int cols[]  = {0,2,4,0,1,3,1,4,0,3,4,0,3};
  int vals[]  = {1,4,1,3,2,1,3,1,4,1,3,1,3};
  int ref[5]  = {29,23,16,28,37};

  int num_rows = 5;

  spmv(num_rows, rows, cols, vals, v, dest);

  for ( int i = 0; i < 5; i++ ) {
    if ( !( dest[i] == ref[i] ) )
      test_fail( i, dest[i], ref[i] );
  }
}

